import React, { useRef } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faMapMarkerAlt,
  faClock,
  faUsers,
} from "@fortawesome/free-solid-svg-icons";
import { Button } from "reactstrap";
import { useNavigate } from "react-router-dom";
import { BASE_URL } from "../utils/config";
import "./searchbar.css";

const SearchBar = () => {
  const locationRef = useRef("");
  
  const navigate = useNavigate();

  const searchHandler = async () => {
    const location = locationRef.current.value;

    if (location === "") {
      return alert("All field are required");
    }

    const res =
      await fetch(`${BASE_URL}/tours/search/getTourBySearch?city=${location}`);

    if (!res.ok) alert("Something went wrong");

    const result = await res.json();

    navigate(
      `/tours/search?city=${location}
    &distance=${distance}&maxGroupSize=${maxGroupSize}`,
      { state: result.data }
    );
  };

  return (
    <div className="search_bar-container">
      <div className="input_container">
        <FontAwesomeIcon icon={faMapMarkerAlt} className="icon" />
        <input
          type="text"
          placeholder="Destination"
          className="search_input"
          ref={locationRef}
        />
      </div>
      <div className="input_container">
        <FontAwesomeIcon icon={faClock} className="icon" />
        <input
          type="text"
          placeholder="Distance"
          className="search_input"
          ref={distanceRef}
        />
      </div>
      <div className="input_container">
        <FontAwesomeIcon icon={faUsers} className="icon" />
        <input
          type="number"
          placeholder="Travelers"
          className="search_input"
          ref={maxGroupSizeRef}
        />
      </div>
      <div>
        <Button className="search" onClick={searchHandler}>
          <i className="ri-search-line"></i>
        </Button>
      </div>
    </div>
  );
};

export default SearchBar;
