import React, { useState } from "react";
import { Container, Row, Col } from "reactstrap";
import "./about.css";
import { motion, AnimatePresence } from "framer-motion";

const About = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <Container>
      <Row>
        <Col>
          <div className="offer">
            <motion.div
              transition={{ layout: { duration: 1, type: "spring" } }}
              Layout
              onClick={() => setIsOpen(!isOpen)}
              className="card"
              style={{
                borderRadius: "1rem",
                boxShadow: "0px 10px 30px (0,0,0,,0.5)",
              }}
            >
              <motion.h2 Layout="position">New Year, New Adventures</motion.h2>
              <AnimatePresence>
              {isOpen && (
                <motion.div
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ duration:1 }}
                  exit={{ opacity }}
                  className="expand"
                >
                  Save 15% Or More When You Book And Stay Before April 1, 2024
                </motion.div>
              )}
              </AnimatePresence>
            </motion.div>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default About;
