import React, { useState, useContext } from "react";
import { motion } from "framer-motion";
import { Link } from "react-router-dom";
import "./tourscard.css";
import { ThemeContext } from "../shared/Theme";

const ToursCard = ({ tour }) => {
  const { _id, title, city, photo, price, avgRating, reviews } = tour; //featured

  const [isOpen, setIsOpen] = useState(false);

  const { theme } = useContext(ThemeContext);

  return (
    <div className={theme === "light" ? "dark-theme" : "light-theme"}>
      <motion.div
        transition={{ layout: { duration: 1, type: "spring" } }}
        layout
        onClick={() => setIsOpen(!isOpen)}
        className="card"
      >
        <motion.img layout="position" src={photo} alt="" />
        {isOpen && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1 }}
            exit={{ opacity: 0 }}
            className="expand"
          >
            <div className="d-flex">
              <div className="tour_city">
                <i className="ri-map-pin-line city"></i> {city}
              </div>
              <div className="tour_rating">
                <i className="ri-star-fill"></i> {avgRating}
                <span>({reviews.length})</span>
              </div>
            </div>
            <div className="tour_price">
              <Link to={`/tours/${_id}`}>{title}</Link>
              <span>${price}/per person</span>
              <button className="booking_btn">
                <Link to={`/tours/${_id}`}>Book Now</Link>
              </button>
            </div>
          </motion.div>
        )}
      </motion.div>
    </div>
  );
};

export default ToursCard;
