import React, { useState } from "react";
import { Container, Row, Col } from "reactstrap";
import "./about.css";
import { motion } from "framer-motion";

const About = () => {

  const [ isOpen, setIsOpen ] = useState(false);

  return (
    <Container>
      <Row>
        <Col>
          <div className="offer">
            <motion.div Layout onClick={() => setIsOpen(!isOpen)} className="card">
              <motion.h2 Layout="pos">New Year, New Adventures</motion.h2>
              {isOpen && (
              <motion.div>
                Save 15% Or More When You Book And Stay Before April 1, 2024
              </motion.div>
              )}
            </motion.div>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default About;
