import React, { useState } from "react";
import { motion } from "framer-motion";
import { Link } from "react-router-dom";
import "./tourscard.css";

const ToursCard = ({ tour }) => {
  const { _id, title, city, photo, price, avgRating, reviews } = tour; //featured

  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="tour_card">
      <motion.div
        transition={{ layout: { duration: 1, type: "spring" } }}
        layout
        onClick={() => setIsOpen(!isOpen)}
        className="card"
      >
        <motion.img layout="position" src={photo} alt="" />
        {isOpen && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1 }}
            exit={{ opacity: 0 }}
            className="expand"
          >
            <div className="tour_icon d-flex align-items">
            <i className="ri-map-pin-line"></i> {city}
            <i className="ri-star-fill"></i> {avgRating}
            <span>({reviews.length})</span>
            </div>
            <Link to={`/tours/${_id}`}>{title}</Link>${price}{" "}
            <span>/per person</span>
            <button className="booking_btn">
              <Link to={`/tours/${_id}`}>Book Now</Link>
            </button>
          </motion.div>
        )}
      </motion.div>
    </div>
  );
};

export default ToursCard;
