import React, { useEffect, useRef, useContext } from "react";
import { Container, Row, Col, Form, ListGroup } from "reactstrap";
import { useParams } from "react-router-dom";
import tourData from "../assets/data/tours";
import "./toursdetails.css";
import avatar from "../assets/images/avatar.jpg";
import Booking from "../components/Booking/Booking";
import Newsletter from "../shared/Newsletter";
import { ThemeContext } from "../shared/Theme";
import { motion, useAnimation, useInView } from "framer-motion";

const ToursDetails = () => {
  const { theme } = useContext(ThemeContext);

  const controls = useAnimation();
  const elementRef = useRef(null);
  const isInView = useInView(elementRef, { once: true });

  useEffect(() => {
    if (isInView) {
      controls.start("visible");
    }
  }, [isInView, controls]);

  const { id } = useParams();

  const tour = tourData.find((tour) => tour.id);

  const {
    title,
    city,
    distance,
    address,
    price,
    maxGroupSize,
    desc,
    reviews,
    avgRating,
    photo,
    featured,
  } = tour;

  const options = { day: "numeric", month: "long", year: "numeric" };

  return (
    <>
      <div
        className={theme === "light" ? "dark-theme" : "light-theme"}
        ref={elementRef}
        style={{ position: "relative" }}
      >
        <motion.section
          variants={{
            hidden: { opacity: 0, y: 75 },
            visible: { opacity: 1, y: 0 },
          }}
          initial="hidden"
          animate={controls}
          transition={{ duration: 1.5, delay: 0.9 }}
        >
          <Container>
            <Row>
              <Col lg="8">
                <div className="tour_content">
                  <img src={photo} alt="" />
                  <div className="tour_info">
                    <h2>{title}</h2>
                    {id}
                    <div className="d-flex align-items-center gap-5">
                      <span className="tour_rating d-flex align-items-center gap-1">
                        <i
                          className="ri-star-fill"
                          style={{ color: "var(--secondary-color)" }}
                        ></i>
                        {avgRating}
                        {featured}
                      </span>

                      <span>
                        <i className="ri-map-pin-line"></i> {address}
                      </span>
                    </div>
                    <div className="tour_details-extra">
                      <span>
                        <i className="ri-map-pin-user-fill"></i>
                        {city}
                      </span>
                      <span>
                        <i className="ri-money-dollar-circle-line"></i>${price}{" "}
                        /per person
                      </span>
                      <span>
                        <i className="ri-pin-distance-line"></i>
                        {distance} k/m
                      </span>
                      <span>
                        <i className="ri-group-line"></i>
                        {maxGroupSize}
                      </span>
                    </div>
                    <h5>Description</h5>
                    <p>{desc}</p>
                  </div>
                  <div className="tour_reviews mt-4">
                    <h4>Reviews({reviews?.length}reviews)</h4>
                    <Form>
                      <div className="d-flex align-items-center gap-3 mb-4 rating_group">
                        <span>
                          <i className="ri-star-fill"></i>
                        </span>
                        <span>
                          <i className="ri-star-fill"></i>
                        </span>
                        <span>
                          <i className="ri-star-fill"></i>
                        </span>
                        <span>
                          <i className="ri-star-fill"></i>
                        </span>
                        <span>
                          <i className="ri-star-fill"></i>
                        </span>
                      </div>

                      <div className="review_input">
                        <input type="text" placeholder="share your thoughts" />
                        <button
                          className="review_submit"
                          type="submit"
                        >
                          Submit
                        </button>
                      </div>
                    </Form>
                    <ListGroup className="user_reviews">
                      {reviews?.map((review, index) => (
                        <div className="review_item" key={review.id}>
                          <img src={avatar} alt="" key={index.id} />

                          <div className="w-100">
                            <div className="d-flex align-items-center justify-content-center">
                              <div>
                                <h5>kathan</h5>
                                <p>
                                  {new Date("01-18-2023").toLocaleDateString(
                                    "en-US",
                                    options
                                  )}
                                </p>
                              </div>
                              <span className="d-flex align-items-center ">
                                <i className="ri-star-fill"></i>
                              </span>
                            </div>
                            <h6>Amazing Tour</h6>
                          </div>
                        </div>
                      ))}
                    </ListGroup>
                  </div>
                </div>
              </Col>
              <Col lg="4">
                <Booking tour={tour} />
              </Col>
            </Row>
          </Container>
        </motion.section>
      </div>
      <Newsletter />
    </>
  );
};

export default ToursDetails;
