import React from "react";
import "./services.css"; // Import the CSS file or use inline styles

const AnimatedFlexCards = () => {
  return (
    <div className="wrapper">
      <div className="container">
        <input type="radio" name="slide" id="c1" checked />
        <label htmlFor="c1" className="card">
          <div className="row">
            <div className="icon">1</div>
            <div className="description">
              <h4>Winter</h4>
              <p>Winter has so much to offer - creative activities</p>
            </div>
          </div>
          <img src="./img1.jpeg" alt="Card 1" />
        </label>
        {/* Repeat the input and label elements for other cards */}
        {/* Ensure to replace background-image URLs with appropriate imports */}
      </div>
    </div>
  );
};

export default AnimatedFlexCards;
