import { useState } from "react";
import "./App.css";
import Layout from "./components/Layout/Layout";
import { ThemeContext } from './ThemeContext'

const App = () => {
  const [ theme, setTheme ] = useState('dark')

  const toggleTheme = () => {
    setTheme((curr) => (curr === "light" ? "dark" : "light"));
  };
  return <Layout />;
}

export default App;
