import React, { useRef, useEffect, useContext } from "react";
import { Container, Row, Button } from "reactstrap";
import { NavLink, Link } from "react-router-dom";
import logo from "../../assets/images/logo.jpg";
import "./header.css";
import { ThemeContext } from "../../shared/Theme";

const nav_links = [
  {
    path: "/home",
    display: "Home",
  },
  {
    path: "/about",
    display: "About",
  },
  {
    path: "/tours",
    display: "Tours",
  },
];

const Header = () => {

  const slides = useAnimation();
  const controls = useAnimation();
  const elementRef = useRef(null);
  const isInView = useInView(elementRef, { once: true });

  useEffect(() => {
    if (isInView) {
      controls.start("visible");
      slides.start("visible");
    }
  }, [isInView]);

  const headerRef = useRef(null);

  const { theme, toggleTheme } = useContext(ThemeContext);

  const stickyHeaderFunc = () => {
    window.addEventListener("scroll", () => {
      if (headerRef.current) {
        if (window.scrollY > 80) {
          headerRef.current.classList.add("sticky_header");
        } else {
          headerRef.current.classList.remove("sticky_header");
        }
      }
    });
  };

  useEffect(() => {
    stickyHeaderFunc();

    return window.removeEventListener("scroll", stickyHeaderFunc);
  });

  return (
    <header className={theme === "dark" ? "dark-theme" : "light-theme"} >
      <section className="header" ref={headerRef}>
        <Container>
          <Row>
            <div className="nav_wrapper d-flex align-items-center justify-content-between">
              <div className="logo">
                <img src={logo} alt="" />
              </div>

              <div className="navigation">
                <ul className="menu d-flex align-items-center gap-5">
                  {nav_links.map((item, index) => (
                    <li className="nav_item" key={index}>
                      <NavLink
                        to={item.path}
                        className={(navClass) =>
                          navClass.isActive ? "active_link" : ""
                        }
                      >
                        {item.display}
                      </NavLink>
                    </li>
                  ))}
                </ul>
              </div>

              <div className="nav-right d-flex align-items-center gap-4">
                <div className="nav_btns d-flex align-items-center gap-4">
                  <Button className="btn secondary_btn">
                    <Link to="/login">Login</Link>
                  </Button>
                  <Button className="btn primary_btn">
                    <Link to="/register">Register</Link>
                  </Button>
                </div>
                <span className="mobile_menu">
                  <i className="ri-menu-line"></i>
                </span>
              </div>
              <button className="switch" onClick={toggleTheme}>
                Switch Mode
              </button>
            </div>
          </Row>
        </Container>
      </section>
    </header>
  );
};

export default Header;
