import React, { useContext, useEffect, useRef } from "react";
import "./about.css";
import { motion, useAnimation, useInView } from "framer-motion";
import { ThemeContext } from "../shared/Theme";

const About = () => {

  const { theme } = useContext(ThemeContext);

  const controls = useAnimation();
  const elementRef = useRef(null);
  const isInView = useInView(elementRef, { once: true });

  useEffect(() => {
    if (isInView) {
      controls.start("visible");
    }
  }, [isInView, controls]);

  return (
    <section
    
    >
    <div className="about_us">
      <p>
        Founded in 2024 in India, Quantum Travel has grown from a small Indian
        start-up to one of the world’s leading digital travel companies.
        Quantum Travel’s mission is to make it easier for everyone to experience
        the world.
        
         By investing in technology that takes the friction out of
        travel, Quantum Travel seamlessly connects millions of travelers to
        memorable experiences, a variety of transportation options, and
        incredible places to stay – from homes to hotels, and much more. As one
        of the world’s largest travel marketplaces for both established brands
        and entrepreneurs of all sizes, Booking.com enables properties around
        the world to reach a global audience and grow their businesses.
      </p>
    </div>
    </section>
  );
};

export default About;
