import React, { useRef, useState } from "react";
import "./searchbar.css";
import { BASE_URL } from "../utils/config";
import { useNavigate } from "react-router-dom";
import {
  Container,
  Row,
  Col,
  Form,
  FormGroup,
  Input,
  Button,
} from "reactstrap";

const SearchBar = () => {
  const [destination, setDestination] = useState("");
  const [dates, setDates] = useState("");
  const [travelers, setTravelers] = useState("");

  const locationRef = useRef("");
  const durationRef = useRef(0);
  const maxGroupSizeRef = useRef(0);
  const navigate = useNavigate();

  const searchHandler = async () => {
    const location = locationRef.current.value;
    const duration = durationRef.current.value;
    const maxGroupSize = maxGroupSizeRef.current.value;

    if (location === "" || duration === "" || maxGroupSize === "") {
      return alert("All field are required");
    }

    const res =
      await fetch(`${BASE_URL}/tours/search/getTourBySearch?city=${location}
    &duration==${duration}&maxGroupSize=${maxGroupSize}`);

    if (!res.ok) alert("Something went wrong");

    const result = await res.json();

    navigate(
      `/tours/search?city=${location}
    &duration==${duration}&maxGroupSize=${maxGroupSize}`,
      { state: result.Data }
    );
  };

  return (
    <>
      <Container>
        <Row className="d-flex justify-content-center">
          <Col md={8}>
            <Form className="bg-light p-4 rounded">
              <FormGroup>
                <Input
                  type="text"
                  placeholder="Destination"
                  value={destination}
                  onChange={(e) => setDestination(e.target.value)}
                />
              </FormGroup>
              <FormGroup>
                <Input
                  type="text"
                  placeholder="Dates"
                  value={dates}
                  onChange={(e) => setDates(e.target.value)}
                />
              </FormGroup>
              <FormGroup>
                <Input
                  type="number"
                  placeholder="Travelers"
                  value={travelers}
                  onChange={(e) => setTravelers(e.target.value)}
                />
              </FormGroup>
              <Button color="primary" onClick={searchHandler}>
                <i className="ri-search-line"></i>
              </Button>
            </Form>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default SearchBar;
