import React from "react";
import { Container, Row, Col } from "reactstrap";
import { useParams } from "react-router-dom";
import TourData from "../assets/data/tours";
import "./toursdetails.css";

const ToursDetails = () => {
  const id = useParams();

  const tour = TourData.find((tour) => tour.id ===   id);

  const { photo, title, desc, price, address, city, distance, maxGroupSize, avgRating } = tour;

  return (
    <section>
      <Container>
        <Row>
          <Col lg="8">
            <div className="tour-content">
              <img src={photo} alt="" />
              <div className="tour_info">
                <h2>{title}</h2>
                <div className="d-flex align-items-center gap-5">
                  <span className="tour_rating d-flex align-item-center gap-1">
                    <i class="ri-star-fill" style={{color: "var(--secondary-color)"}}></i> {avgRating}
                  </span>

                  <span>
                  <i class="ri-map-pin-line"></i> {address}
                  </span>
                </div>
                <div className="tour_details-extra">
                  <span><i class="ri-map-pin-user-fill"></i>{city}</span>
                  <span><i class="ri-money-dollar-circle-line"></i>${price}</span>
                  <span><i class="ri-group-line"></i>{maxGroupSize}</span>
                </div>
                <h5>Description</h5>
                <p>{desc}</p>
              </div>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default ToursDetails;
