import React from "react";
import { Card, CardBody } from "reactstrap";
import { Link } from "react-router-dom";
import "./tourscard.css";

const ToursCard = ({ tour }) => {
  const { _id, title, city, photo, price, avgRating, reviews } = tour; //featured

  return (
    <div className="tour_card">
      <motion.div
          transition={{ layout: { duration: 1, type: "spring" } }}
          layout
          onClick={() => setIsOpen(!isOpen)}
          className="card"
        >
          <motion.img layout="position">New Year, New Adventures</motion.img>
          {isOpen && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 1 }}
              exit={{ opacity: 0 }}
              className="expand"
            >
              <p>Save 15% Or More When You Book And Stay Before April 1, 2024</p>
            </motion.div>
          )}
        </motion.div>
      <Card>
        <div className="tour_img">
          <img src={photo} alt="tour-img" />
          <span>Featured</span>
        </div>

        <CardBody>
          <div className="card_top d-flex align-items-center justify-content-between">
            <span className="tour_location d-flex align-items-center gap-1">
              <i className="ri-map-pin-line"></i> {city}
            </span>
            <span className="tour_location d-flex align-items-center gap-1">
              <i className="ri-star-fill"></i> {avgRating}
              <span>({reviews.length})</span>
            </span>
          </div>
          <h5 className="tour_title">
            <Link to={`/tours/${_id}`}>{title}</Link>
          </h5>
          <div
            className="card_bottom"
            d-flex="true"
            align-items-center="true"
            justify-content-between="true"
            mt-3="true"
          >
            <h5>
              ${price} <span>/per person</span>
            </h5>

            <button className="btn booking_btn">
              <Link to={`/tours/${_id}`}>Book Now</Link>
            </button>
          </div>
        </CardBody>
      </Card>
    </div>
  );
};

export default ToursCard;
