import { createContext, useEffect, useReducer } from "react";

// const initial_state = {
//   user:
//     localStorage.getItem("user") !== undefined
//       ? JSON.parse(localStorage.getItem("user"))
//       : null,
//   loading: false,
//   error: null,
// };

export const AuthContext = createContext(initial_state);

const AuthReducer = (state, action) => {
  switch (action.type) {
    case "Login_start":
      return {
        user: null,
        loading: true,
        error: null,
      };
    case "Login_Success":
      return {
        user: action.payload,
        loading: false,
        error: null,
      };
    case "Login_Failure":
      return {
        user: null,
        loading: false,
        error: action.payload,
      };
    case "Register_Success":
      return {
        user: null,
        loading: false,
        error: null,
      };
    case "Logout":
      return {
        user: null,
        loading: false,
        error: null,
      };
    default:
      return state;
  }
};

