import React, { useRef, useEffect, useContext } from "react";
import { Button, Container, Row } from "reactstrap";
import { Link, useNavigate } from "react-router-dom";
import "./header.css";
import SearchBar from "../../shared/SearchBar";
import { ThemeContext } from "../../shared/Theme";
import { motion, useAnimation, useInView } from "framer-motion";
import ReactSwitch from "react-switch";
import { AuthContext } from "./../../context/AuthContext";

const Header = () => {
  const controls = useAnimation();
  const elementRef = useRef(null);
  const isInView = useInView(elementRef, { once: true });

  useEffect(() => {
    if (isInView) {
      controls.start("visible");
    }
  }, [isInView, controls]);

  const headerRef = useRef(null);
  const navigate = useNavigate()
  const { user, dispatch } = useContext(AuthContext)

  const Logout = ()=>{
    dispatch({type:'Logout'})
    navigate('/')
  }

  const { theme, toggleTheme } = useContext(ThemeContext);

  const stickyHeaderFunc = () => {
    if (headerRef.current) {
      if (window.scrollY > 80) {
        headerRef.current.classList.add("sticky_header");
      } else {
        headerRef.current.classList.remove("sticky_header");
      }
    }
  };

  useEffect(() => {
    stickyHeaderFunc();

    return window.removeEventListener("scroll", stickyHeaderFunc);
  });

  return (
    <header
      className={theme === "light" ? "dark-theme" : "light-theme"}
      ref={elementRef}
      style={{ position: "relative", width: "", overflow: "hidden" }}
    >
      <motion.section
        className="header"
        ref={headerRef}
        variants={{
          hidden: { opacity: 0, y: 75 },
          visible: { opacity: 1, y: 0 },
        }}
        initial="hidden"
        animate={controls}
        transition={{ duration: 0.5, delay: 0.25 }}
      >
        <Container>
          <Row className="pt-3">
            <div className="nav_wrapper d-flex justify-content-between">
              <div className="logo">
                <h1 className="subtitle">Quantum Travel</h1>
              </div>

              <div className="navigation d-flex align-items-center gap-4">
                <ul className="nav_item">
                  <Link to="/home">Home</Link>
                </ul>
                <ul className="nav_item">
                  <Link to="/about">About</Link>
                </ul>
                <ul className="nav_item">
                  <Link to="/tours">Tours</Link>
                </ul>
              </div>

              <div className="d-flex align-items-center justify-content-between">
                <SearchBar />

                {user? (<> 
                    <h5>{user.username}</h5>
                    <Button className="logout_btn" onClick={Logout}>Logout</Button>
                  </> 
                  ) : ( 
                  <>
                  <Link
                  to="/login"
                  className="account d-flex align-items-center justify-content-between gap-3"
                ><i className="ri-account-circle-line"></i></Link>
                  </>
                )}
                  <div>
                    <ReactSwitch
                      onChange={toggleTheme}
                      checked={theme === "dark"}
                    />
                  </div> 
              </div>
            </div>
          </Row>
        </Container>
      </motion.section>
    </header>
  );
};

export default Header;
