import React, { useRef } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faMapMarkerAlt,
  faCalendarAlt,
  faUsers,
} from "@fortawesome/free-solid-svg-icons";
import { Button } from "reactstrap";
import { useNavigate } from "react-router-dom";
import { BASE_URL } from '../utils/config'
import "./searchbar.css";

const SearchBar = () => {
  const locationRef = useRef("");
  const durationRef = useRef(0);
  const maxGroupSizeRef = useRef(0);
  const navigate = useNavigate();

  const searchHandler = async () => {
    const location = locationRef.current.value;
    const duration = durationRef.current.value;
    const maxGroupSize = maxGroupSizeRef.current.value;

    if (location === "" || duration === "" || maxGroupSize === "") {
      return alert("All field are required");
    }

    const res =
      await fetch(`${BASE_URL}/tours/search/getTourBySearch?city=${location}
    &duration==${duration}&maxGroupSize=${maxGroupSize}`);

    if (!res.ok) alert("Something went wrong");

    const result = await res.json();

    navigate(
      `/tours/search?city=${location}
    &duration==${duration}&maxGroupSize=${maxGroupSize}`,
      { state: result.Data }
    );
  };

  return (
    <div className="search_bar-container">
      <div className="input-container">
        <FontAwesomeIcon icon={faMapMarkerAlt} className="icon" />
        <input
          type="text"
          placeholder="Destination"
          className="search-input"
          ref={locationRef}
        />
      </div>
      <div className="input-container">
        <FontAwesomeIcon icon={faCalendarAlt} className="icon" />
        <input
          type="date"
          className="search-input date-input"
          ref={durationRef}
        />
      </div>
      <div className="input-container">
        <FontAwesomeIcon icon={faUsers} className="icon" />
        <input
          type="number"
          placeholder="Travelers"
          className="search-input"
          ref={maxGroupSizeRef}
        />
      </div>
      <div>
        <Button className="search" onClick={searchHandler}>
          <i className="ri-search-line"></i>
        </Button>
      </div>
    </div>
  );
};

export default SearchBar;
