import React, { useRef } from "react";
import "./searchbar.css";
import { Col, Container, Form, FormGroup, Row } from "reactstrap";
import { BASE_URL } from "../utils/config";
import { useNavigate } from "react-router-dom";

const SearchBar = () => {
  const locationRef = useRef("");
  const durationRef = useRef(0);
  const maxGroupSizeRef = useRef(0);
  const navigate = useNavigate();

  const searchHandler = async () => {
    const location = locationRef.current.value;
    const duration = durationRef.current.value;
    const maxGroupSize = maxGroupSizeRef.current.value;

    if (location === "" || duration === "" || maxGroupSize === "") {
      return alert("All field are required");
    }

    const res =
      await fetch(`${BASE_URL}/tours/search/getTourBySearch?city=${location}
    &duration==${duration}&maxGroupSize=${maxGroupSize}`);

    if (!res.ok) alert("Something went wrong");

    const result = await res.json();

    navigate(
      `/tours/search?city=${location}
    &duration==${duration}&maxGroupSize=${maxGroupSize}`,
      { state: result.Data }
    );
  };

  return (
    <section>
      <Container>
        <Row>
          <Col lg="14">
            <div className="search_bar d-flex gap-10">
                <FormGroup className="d-flex align-items-center">
                  <span>
                    <i className="ri-map-pin-line"></i>
                  </span>
                  <div>
                    <h6>Location</h6>
                    <input
                      type="text"
                      placeholder="Where are you going?"
                      ref={locationRef}
                    ></input>
                  </div>
                </FormGroup>
                <FormGroup className="d-flex gap-3">
                  <span>
                    <i className="ri-calendar-schedule-line"></i>
                  </span>
                  <div>
                    <h6>Duration</h6>
                    <input
                      type="date"
                      placeholder="Start Date"
                      ref={durationRef}
                    />
                  </div>
                  <br></br>
                </FormGroup>
                <FormGroup className="d-flex gap-3 form_group form_group-last">
                  <span>
                    <i className="ri-group-line"></i>
                  </span>
                  <div>
                    <h6>Total People</h6>
                    <input
                      type="number"
                      placeholder="0"
                      ref={maxGroupSizeRef}
                    ></input>
                  </div>
                </FormGroup>
                <span
                  className="search_icon"
                  type="submit"
                  onClick={searchHandler}
                >
                  <i className="ri-search-line"></i>
                </span>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default SearchBar;
