import React, { useState } from "react";
import "./services.css";
import { motion } from "framer-motion";

const ServicesCard = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <>
      <div className="offer">
        <motion.div
          transition={{ layout: { duration: 1, type: "spring" } }}
          layout
          onClick={() => setIsOpen(!isOpen)}
          className="card"
          style={{
            borderRadius: "",
            boxShadow: "0px 10px 30px (0, 0, 0, 0.5)",
          }}
        >
          <motion.h2 layout="position">New Year, New Adventures</motion.h2>
          {isOpen && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 1 }}
              exit={{ opacity: 0 }}
              className="expand"
            >
              <p>Save 15% Or More When You Book And Stay Before April 1, 2024</p>
            </motion.div>
          )}
        </motion.div>
      </div>
      <div className="offer">
        <motion.div
          transition={{ layout: { duration: 1, type: "spring" } }}
          layout
          onClick={() => setIsOpen(!isOpen)}
          className="card"
          style={{
            borderRadius: "1rem",
            boxShadow: "0px 10px 30px (0, 0, 0, 0.5)",
          }}
        >
          <motion.h2 layout="position">Get Instant Discounts</motion.h2>
          {isOpen && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 1 }}
              exit={{ opacity: 0 }}
              className="expand"
            >
              <p>Just Sign Into Your Booking.com Account</p>
            </motion.div>
          )}
        </motion.div>
      </div>
      <div className="offer">
        <motion.div
          transition={{ layout: { duration: 1, type: "spring" } }}
          layout
          onClick={() => setIsOpen(!isOpen)}
          className="card"
          style={{
            borderRadius: "1rem",
            boxShadow: "0px 10px 30px (0, 0, 0, 0.5)",
          }}
        >
          <motion.h2 layout="position">Ayodhya Special</motion.h2>
          {isOpen && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 1 }}
              exit={{ opacity: 0 }}
              className="expand"
            >
              <p>Enjoy Epic Offers On Booking Buses, Cabs & Trains</p>
            </motion.div>
          )}
        </motion.div>
      </div>
    </>
  );
};

export default ServicesCard;
