import React, { useState } from "react";
import "./login.css";
import { Container, Row, Col, FormGroup, Button, Form } from "reactstrap";
import { Link } from "react-router-dom";
import loginImg from "../assets/images/login.png";
import userIcon from "./assets/images/user.png";

const Login = () => {

  const [ Credentials, setCredentials ] =useState({
    userID: '01',
    userEmail: 'ex'
  })

  const handleChange = e => {
    setCredentials(prev => ({ ...prev,[e.traget.id]: e.traget.value}));
  };

  return (
    <section>
      <Row>
        <Col lg="8" className="m-auto">
          <div className="login_container d-flex align-items-center justify-content-between">
            <div className="login_img">
              <img src={loginImg} alt="" />
            </div>
            <div className="login_form">
              <div className="user">
                <img src={userIcon} alt="" />
              </div>
              <h2>Login</h2>

              <Form >
                <FormGroup>
                  <input type="email" placeholder="email" required id="email" onChange={handleChange}/>
                  <input type="password" placeholder="password" required id="password" onChange={handleChange}/>
                </FormGroup>
                <Button className="btn secondary_btn auth_btn" type="submit">Login</Button>
              </Form>
              <p>Don't Have An Account? <Link to='/register'>Create</Link></p>
            </div>
          </div>
        </Col>
      </Row>
    </section>
  );
};

export default Login;
