import React, { useRef } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faMapMarkerAlt,
  faCalendarAlt,
  faUsers,
} from "@fortawesome/free-solid-svg-icons";

import "./searchbar.css";

const SearchBar = () => {
  return (
    <div className="search-bar-container">
      <div className="input-container">
        <FontAwesomeIcon icon={faMapMarkerAlt} className="icon" />
        <input type="text" placeholder="Destination" className="search-input" />
      </div>
      <div className="input-container">
        <FontAwesomeIcon icon={faCalendarAlt} className="icon" />
        <input type="date" className="search-input date-input" />
      </div>
      <div className="input-container">
        <FontAwesomeIcon icon={faUsers} className="icon" />
        <input type="number" placeholder="Travelers" className="search-input" />
      </div>
      <div>
        <Button className="search" onClick={searchHandler}>
          <i className="ri-search-line"></i>
        </Button>
      </div>
    </div>
  );
};

export default SearchBar;
