import React, { useState,useContext } from "react";
import "./booking.css";
import { Form, FormGroup, ListGroupItem, Button } from "reactstrap";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import { BASE_URL } from "../../utils/config";

const Booking = ({ tour }) => {
  const { price, reviews, avgRating } = tour;
  const navigate = useNavigate();
  const {user} = useContext(AuthContext)



  const [booking, setBooking] = useState({
    userId: "01",
    userEmail: "example@gmail.com",
    fullName: "",
    phone: "",
    guestSize: 1,
    bookAt: "",
  });

  const handlechange = (e) => {
    setBooking((prev) => ({ ...prev, [e.target.id]: e.target.value }));
  };

  const serviceFee = 10;
  const totalAmount =
    Number(price) * Number(booking.guestSize) + Number(serviceFee);

  const handleClick = async(e) => {
    e.preventDefault();

    try {
      if(!user || user===undefined || user===null)
      return alert('Please Sign in')

      const res = await fetch(`${BASE_URL}/reviewa`)
    } catch (error) {
      
    }

    navigate("/thank-you");
  };

  return (
    <>
      <div className="booking">
        <div className="booking_top d-flex align-items-center justify-content-between">
          <h3>
            ${price}
            <span>/per person</span>
          </h3>
          <span className="tour_rating d-flex align-items-center">
            <i className="ri-star-s-fill"></i>
            {avgRating === 0 ? null : avgRating} ({reviews?.length})
          </span>
        </div>
        <div className="booking_form">
          <h5>Information</h5>
          <Form className="booking_form-info" onSubmit={handleClick}>
            <FormGroup>
              <input
                type="text"
                placeholder="Full Name"
                id="fullName"
                required
                onChange={handlechange}
              />
            </FormGroup>
            <FormGroup>
              <input
                type="number"
                placeholder="Phone"
                id="phone"
                required
                onChange={handlechange}
              />
            </FormGroup>
            <FormGroup className="d-flex align-items-center gap-3">
              <input
                type="date"
                placeholder=""
                id="bookAt"
                required
                onChange={handlechange}
              />
              <input
                type="number"
                placeholder="Members"
                id="guestSize"
                required
                onChange={handlechange}
              />
            </FormGroup>
          </Form>
        </div>
        <div className="booking_bottom">
          <ListGroupItem className="border-0 px-0">
            <h5 className="d-flex align-items-center gap-1">
              ${price} <i className="ri-close-line"></i> per person
            </h5>
            <span>${price}</span>
          </ListGroupItem>
          <ListGroupItem className="border-0 px-0">
            <h5>Services Charge</h5>
            <span>${serviceFee}</span>
          </ListGroupItem>
          <ListGroupItem className="border-0 px-0 total">
            <h5>Total</h5>
            <span>${totalAmount}</span>
          </ListGroupItem>
          <Button className="btn primary_btn w-100 mt-4" onClick={handleClick}>
            Book Now
          </Button>
        </div>
      </div>
    </>
  );
};

export default Booking;
