import React, { useState } from 'react';
import './about.css'
import offer1 from '../assets/images/offer1.jpeg'
import offer2 from '../assets/images/offer2.webp'
import offer3 from '../assets/images/offer3.avif'

const About = () => {

  const handleCardHover = (index) => {
    setIsExpanded((prevExpanded) =>
      prevExpanded.length === 0
        ? [index] // Open the hovered card if none are open
        : prevExpanded.includes(index)
          ? prevExpanded.filter((i) => i !== index) // Close the hovered card if open
          : [...prevExpanded, index] // Open the hovered card
    );
  };

  return (
    <Row xs={1} md={2} className="g-4">
      {Array.from({ length: 3 }).map((_, idx) => (
        <Col key={idx} className="mb-3">
          <div
            className={`card-container ${isExpanded.includes(idx) ? 'expanded' : ''}`}
            onMouseEnter={() => handleCardHover(idx)}
            onMouseLeave={() => handleCardHover(idx)}
          >
            <Card>
              <Card.Img variant="top" src={offer1} />
              <Card.Body className="card-content">
                <Card.Title className="card-title">Card title {idx + 1}</Card.Title>
                <Card.Text className="card-text">
                  This is a longer card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.
                </Card.Text>
              </Card.Body>
            </Card>
          </div>
        </Col>
      ))}
    </Row>
  );
}

export default About;
