import React, { useState } from "react";
import "./servicescard.css";
import { motion } from "framer-motion";

const ServicesCard = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <>
      <div className="d-flex">
        <div className="offer">
          <motion.div
            transition={{ layout: { duration: 1, type: "spring" } }}
            layout
            onClick={() => setIsOpen(!isOpen)}
            className="card"
          >
            <motion.h2 layout="position">New Year, New Adventures</motion.h2>
            {isOpen && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 1 }}
                exit={{ opacity: 0 }}
                className="expand"
              >
                <p>
                  Save 15% Or More When You Book And Stay Before April 1, 2024
                </p>
              </motion.div>
            )}
          </motion.div>
          <motion.div
            transition={{ layout: { duration: 1, type: "spring" } }}
            layout
            onClick={() => setIsOpen(!isOpen)}
            className="card"
          >
            <motion.h2 layout="position">Get Instant Discounts</motion.h2>
            {isOpen && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 1 }}
                exit={{ opacity: 0 }}
                className="expand"
              >
                <p>Sign In To Save 10% Or More With A Free Booking.com Membership</p>
              </motion.div>
            )}
          </motion.div>
          <motion.div
            transition={{ layout: { duration: 1, type: "spring" } }}
            layout
            onClick={() => setIsOpen(!isOpen)}
            className="card"
          >
            <motion.h2 layout="position">Ayodhya Special</motion.h2>
            {isOpen && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 1 }}
                exit={{ opacity: 0 }}
                className="expand"
              >
                <p>Enjoy Epic Offers On Booking Buses, Cabs & Trains</p>
              </motion.div>
            )}
          </motion.div>
        </div>
        <div className="offer">
          <motion.div
            transition={{ layout: { duration: 1, type: "spring" } }}
            layout
            onClick={() => setIsOpen(!isOpen)}
            className="card"
          >
            <motion.h2 layout="position">Flat 45% OFF* On Holiday Packages</motion.h2>
            {isOpen && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 1 }}
                exit={{ opacity: 0 }}
                className="expand"
              >
                <p>
                  This Summer, Big Savings On Trips & Memories Will Go Hand-on-Hand! 
                </p>
              </motion.div>
            )}
          </motion.div>
          <motion.div
            transition={{ layout: { duration: 1, type: "spring" } }}
            layout
            onClick={() => setIsOpen(!isOpen)}
            className="card"
          >
            <motion.h2 layout="position">Special Discounts On Singapore Hotels</motion.h2>
            {isOpen && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 1 }}
                exit={{ opacity: 0 }}
                className="expand"
              >
                <p>Grab Up To 25% OFF* On Hotels In Singapore</p>
              </motion.div>
            )}
          </motion.div>
          <motion.div
            transition={{ layout: { duration: 1, type: "spring" } }}
            layout
            onClick={() => setIsOpen(!isOpen)}
            className="card"
          >
            <motion.h2 layout="position">Your Next Dream Holiday Starts Here</motion.h2>
            {isOpen && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 1 }}
                exit={{ opacity: 0 }}
                className="expand"
              >
                <p>Get Discounts on DBS Bank Credit Cards</p>
              </motion.div>
            )}
          </motion.div>
        </div>
      </div>
    </>
  );
};

export default ServicesCard;
