import React, { useState } from "react";
import { Container, Row, Col } from "reactstrap";
import "./about.css";
import { motion } from "framer-motion";

const About = () => {

  const { isOpen, setIsOpen } = useState(false)
  return (
    <Container>
      <Row>
        <Col>
          <div className="offer">
            <motion.div className="card">
              <motion.h2>New Year, New Adventures</motion.h2>
              <motion.div>
                Save 15% Or More When You Book And Stay Before April 1, 2024
              </motion.div>
            </motion.div>
          </div>
          <div className="offer">
            <motion.div className="card">
              <motion.h2>Get Instant Discounts</motion.h2>
              <motion.div>Just Sign Into Your Booking.com Account </motion.div>
            </motion.div>
          </div>
          <div className="offer">
            <motion.div className="card">
              <motion.h2>Ayodhya Special</motion.h2>
              <motion.div>
                Enjoy Epic Offers On Booking Buses, Cabs & Trains
              </motion.div>
            </motion.div>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default About;
