import React, { useState, useContext } from "react";
import { motion } from "framer-motion";
import { Link } from "react-router-dom";
import { Button } from "reactstrap";
import "./tourscard.css";
import { ThemeContext } from "../shared/Theme";
import { useAnimation, useInView } from "framer-motion";

const ToursCard = ({ tour }) => {
  const { _id, title, city, photo, price, avgRating, reviews } = tour; //featured

  const [isOpen, setIsOpen] = useState(false);

  const { theme } = useContext(ThemeContext);

  const controls = useAnimation();
  const elementRef = useRef(null);
  const isInView = useInView(elementRef, { once: true });

  useEffect(() => {
    if (isInView) {
      controls.start("visible");
    }
  }, [isInView, controls]);

  return (
    <div
      className={theme === "light" ? "dark-theme" : "light-theme"}
      ref={elementRef}
      style={{ position: "relative" }}
    >
      <motion.section
        variants={{
          hidden: { opacity: 0, y: 75 },
          visible: { opacity: 1, y: 0 },
        }}
        initial="hidden"
        animate={controls}
        transition={{ duration: 1.5, delay: 0.9 }}
      >
        <motion.div
          transition={{ layout: { duration: 1, type: "spring" } }}
          layout
          onClick={() => setIsOpen(!isOpen)}
          className="card"
        >
          <motion.img layout="position" src={photo} alt="" />
          {isOpen && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 1 }}
              exit={{ opacity: 0 }}
              className="expand"
            >
              <div className="d-flex justify-content-between">
                <div className="tour_city">
                  <i className="ri-map-pin-line city"></i> {city}
                </div>
                <div className="tour_rating">
                  <i className="ri-star-fill"></i> {avgRating}
                  <span>({reviews.length})</span>
                </div>
              </div>
              <div className="tour_price">
                <Link to={`/tours/${_id}`}>{title}</Link>
                <br />
                <span>${price}/per person</span>
                <Button className="booking_btn">
                  <Link to={`/tours/${_id}`}>Book Now</Link>
                </Button>
              </div>
            </motion.div>
          )}
        </motion.div>
      </motion.section>
    </div>
  );
};

export default ToursCard;
