import React, { useRef, useEffect, useContext } from "react";
import { Container, Row, Button } from "reactstrap";
import { Link } from "react-router-dom";
import "./header.css";
import { ThemeContext } from "../../shared/Theme";
import { motion, useAnimation, useInView } from "framer-motion";

const Header = () => {
  const controls = useAnimation();
  const elementRef = useRef(null);
  const isInView = useInView(elementRef, { once: true });

  useEffect(() => {
    if (isInView) {
      controls.start("visible");
    }
  }, [isInView, controls]);

  const headerRef = useRef(null);

  const { theme, toggleTheme } = useContext(ThemeContext);

  const stickyHeaderFunc = () => {
    window.addEventListener("scroll", () => {
      if (headerRef.current) {
        if (window.scrollY > 80) {
          headerRef.current.classList.add("sticky_header");
        } else {
          headerRef.current.classList.remove("sticky_header");
        }
      }
    });
  };

  useEffect(() => {
    stickyHeaderFunc();

    return window.removeEventListener("scroll", stickyHeaderFunc);
  });

  return (
    <header
      className={theme === "light" ? "dark-theme" : "light-theme"}
      ref={elementRef}
      style={{ position: "relative", width: "", overflow: "hidden" }}
    >
      <motion.section
        className="header"
        ref={headerRef}
        variants={{
          hidden: { opacity: 0, y: 75 },
          visible: { opacity: 1, y: 0 },
        }}
        initial="hidden"
        animate={controls}
        transition={{ duration: 0.5, delay: 0.25 }}
      >
        <Container>
          <Row>
            <div className="nav_wrapper d-flex align-items-center justify-content-between">
              <div className="logo">
                <h1 className="subtitle">Quantum Travel</h1>
              </div>

              <div className="navigation">
                <ul className="menu d-flex align-items-center gap-5">
                  <li className="nav_item align-items-center g">
                    <Link to="/home">
                      Home
                    </Link>
                  </li>
                  <li className="nav_item">
                    <Link to="/about">
                      About
                    </Link>
                  </li>
                  <li className="nav_item">
                    <Link onClick={() => (window.location.href = "/tours")}>
                      Tours
                    </Link>
                  </li>
                </ul>
              </div>

              <div className="nav-right">
                <div className="nav_btns">
                  <Button className="btn primary_btn" onClick={toggleTheme}>
                    Switch Mode
                  </Button>
                  <Link to="/login" className="account">
                    <i className="ri-account-circle-line"></i>
                  </Link>
                  <span className="mobile_menu">
                    <i className="ri-menu-line"></i>
                  </span>
                </div>
              </div>
            </div>
          </Row>
        </Container>
      </motion.section>
    </header>
  );
};

export default Header;
